INSERT INTO `categories` (`id`, `name`, `slug`, `desc`, `status`) VALUES
(1, 'Tiên Hiệp', 'tien-hiep', 'Truyện tiên hiệp thường kể về quá trình tu luyện và khám phá thế giới tu sĩ thần tiên đầy bí ẩn của nhân vật chính', 1),
(2, 'Xuyên Không', 'xuyen-khong', 'Xuyên Không, Xuyên Việt là thể loại nhân vật chính vì một lý do nào đó mà bị đưa đến sinh sống ở một không gian hay một khoảng thời gian khác.\r\n\r\nNhân vật chính có thể trực tiếp xuyên qua bằng thân xác mình hoặc sống lại bằng thân xác người khác.', 1),
(5, 'Khoa Huyễn', 'khoa-huyen', 'Những bộ truyện có yếu tố khoa học bí ẩn.', 1),
(6, 'Võng Du', 'vong-du', 'Là thể loại truyện thuộc dạng khoa học viễn tượng, lấy bối cảnh thường là các game online trên mạng với công nghệ cao, hình ảnh chất lượng cao, kỹ xảo đồ sộ, mức chân thật cao, kỳ ảo, và tác giả của thể loại này thường rất giàu trí tưởng tượng', 1),
(9, 'Mạt Thế', 'mat-the', 'Là truyện có bối cảnh tận thế, thế giới bị sụp đổ và diệt vong vì những thảm họa.', 1),
(10, 'Dị Giới', 'di-gioi', 'Trong truyện có xuất hiện những thế giới kỳ dị, khác với thế giới chúng ta đang sinh sống.', 1);

